import axios from "axios";
import React,{useState,useEffect} from "react";
import dotenv from 'dotenv';

const Home = () => {
  dotenv.config();
const [topRatedMovies, setTopRatedMovies] = useState([]);
useEffect(() => {
  const date=new Date();
  const formattedDate= `${date.getFullYear()}-${('0'+(date.getMonth()+1)).slice(-2)}-${('0'+date.getDate()).slice(-2)}`;
  // const currentDate = new Date();
  // const date = currentDate.toLocaleDateString();
  // axios.get(`http://www.omdbapi.com/?i=tt3896198&apikey=a5129865&s=&type=movie,series&y=${formattedDate}&page=1`)
  axios.get(`https://api.themoviedb.org/3/movie/popular?api_key=${process.env.TMDB_API_KEY}&language=en-US&page=1`)
  .then(res=>{
    console.log("res.data: ",res.data)
    // console.log("res.data.Search: ",res.data.Search)
    setTopRatedMovies(res.data.Search)
  })
.catch(err=>console.log(err))
}, [])

  return (
    <div>
      <div className="hero">
        <img src="./images/heroImg.jpg" alt="" className="img-fluid" />
      </div>
    </div>
  );
};

export default Home;
