import React,{useState} from 'react'

const Form = () => {
    const [title, setTitle] = useState('')
    const [director, setDirector] = useState('')
    const [rating, setRating] = useState('')
    const [genre, setGenre] = useState('')
    const [releaseYear, setReleaseYear] = useState('')
    const [duration, setDuration] = useState('')
    const [image, setImage] = useState('')
    const submitHandler=(e)=>{
        e.preventDefault()
    }
  return (
    <div className='col-6 mx-auto'>
        <form >
            <label className='form-label'>Title:</label>
            <input type="text" className='form-control' value={title} onChange={(e)=>setTitle(e.target.value)} />
            <label className='form-label'>Director:</label>
            <input type="text" className='form-control' value={director} onChange={(e)=>setDirector(e.target.value)} />
            <label className='form-label'>Rating:</label>
            <select></select>
            <input type="text" className='form-control' value={rating} onChange={(e)=>setRating(e.target.value)} />
            <label className='form-label'>Genre:</label>
            <input type="text" className='form-control' value={genre} onChange={(e)=>setGenre(e.target.value)} />
            <label className='form-label'>Release Year:</label>
            <input type="text" className='form-control' value={releaseYear} onChange={(e)=>setReleaseYear(e.target.value)} />
            <label className='form-label'>Duration:</label>
            <input type="text" className='form-control' value={duration} onChange={(e)=>setDuration(e.target.value)} />
            <label className='form-label'>Image:</label>
            <input type="text" className='form-control' value={image} onChange={(e)=>setImage(e.target.value)} />
            <button type='submit' className='btn btn-info mt-3 w-100'>Create Movie</button>
        </form>
    </div>
  )
}

export default Form
