import React, { useEffect, useState } from "react";
import axios from "axios";
import { useParams } from "react-router-dom";

const OneMovie = () => {
  const { id } = useParams();
  const [movie, setMovie] = useState({});

  useEffect(() => {
    axios
      .get(`http://localhost:8000/api/movie/${id}`)
      .then((res) => {
        setMovie(res.data);
      })
      .catch((err) => {
        console.log(err);
      });
  }, []);

  return (
    <div className="container d-flex flex-column justify-content-center align-items-center">
      <h1 className="oneMovieTitle">{movie.title}</h1>
     <div className="container d-flex">
     <img src={movie.BoxArt} alt={movie.title}  className="oneMovieImage"/>
    <div className="container">
    <p>Directors: {movie.director}</p>
    <p>{movie.rating}, {movie.genre}, {movie.duration}</p>
    </div>
     </div>
    </div>
  );
};

export default OneMovie;
