import React,{useState} from "react";
import axios from "axios";

const Search = ({getMovie}) => {
  const [text, setText] = useState("")
  const submitHandler= async(e)=>{
    e.preventDefault();
    if(text.trim()!== ""){
      const options = {
        method: 'GET',
        url: 'https://ott-details.p.rapidapi.com/advancedsearch',
        params: {
          start_year: '1970',
          end_year: '2023',
          min_imdb: '6.5',
          max_imdb: '7.8',
          genre: 'action, horror, romance, comedy, drama, sci-fi, family, animated,  documentary, ',
          language: 'english',
          type: 'movie, TV series, show ',
          sort: 'latest',
          page: '1'
        },
        headers: {
          'X-RapidAPI-Key': 'ed3d07424amsh136e1e7e08e3ffep1fd676jsnb7debdfbe560',
          'X-RapidAPI-Host': 'ott-details.p.rapidapi.com'
        }
      };

      axios.request(options).then(function (response) {
        console.log("response.data",response.data);
      }).catch(function (error) {
        console.error(error);
      }); 
      getMovie(text);
      // setText("")
    }
  }
  return (
    <form onSubmit={submitHandler} className="searchForm">
      <input type="text" value={text} placeholder="Search A Movie" onChange={(e)=>setText(e.target.value)} className="searchInput me-1"/>
      <button className="btn btn-light"><i className="fa fa-search" aria-hidden="true"></i></button>
    </form>
  )
};

export default Search;
